    private void statsButtonActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_statsButtonActionPerformed
    {//GEN-HEADEREND:event_statsButtonActionPerformed
        // determine value for gender if entered as 'm' 'M' or 'f' 'F'
//        int gender;
//        if (genderTextField.getText().equals("M") ||
//            genderTextField.getText().equals("m"))
//            gender = 0;
//        else
//            gender = 1;
        // create new object
        person = new Person(Integer.valueOf(ageTextField.getText()),
                Double.valueOf(heightTextField.getText()),
                Double.valueOf(bodyMassTextField.getText()),
                Integer.valueOf(genderTextField.getText()));

        // update view
        normalWeightLabel.setText(String.valueOf(person.getNormalWeight()));
        idealWeightLabel.setText(String.valueOf(person.getIdealWeight()));
        BMILabel.setText(String.valueOf(person.getBMI()));
        categoryLabel.setText(person.getComment());
    }//GEN-LAST:event_statsButtonActionPerformed

